import React, { useEffect,useContext } from 'react'
import ContactsBook from '../apis/ContactsBook'
import { ContactsContext } from '../context/ContactsContext';
import { useNavigate } from 'react-router-dom';

export const ContactList = (props) => {
        const { contacts,setContacts } = useContext(ContactsContext);
        let navigate = useNavigate()
        // eslint-disable-next-line react-hooks/exhaustive-deps
        useEffect(  () => {
            const fetchData = async () => {
                   try {
                    const response = await ContactsBook.get('/')
                    console.log(response)
                    setContacts(response.data.data.people)
                } catch (err) {
                    console.log(err)
                }
            };
            fetchData();
            }
             ,[])
        const handleDelete = async  (id) => {
            try {
                const response = await ContactsBook.delete(`/${id}`);
                setContacts( contacts.filter( contact => {
                    return contact.id !== id
                }))
                console.log(response);
            } catch(err) {
                console.log(err)
            }
        }

        const handleUpdate = (id) => {
            navigate(`/contacts/${id}/update`)
        }

  return (
    <div>
        <table className='table  table-hover'>
            <thead>
                <tr>
                    <th scope="col">First Name</th>
                    <th scope="col">Last Name</th>
                    <th scope="col">Phone Number</th>
                    <th scope="col">Update</th>
                    <th scope="col">Delete</th>
                </tr>
            </thead>
            <tbody>
                { contacts && contacts.map( contact  =>  {
                    return(
                        <tr key={ contact.id }>
                            <td>{ contact.name }</td>
                            <td>{ contact.last_name}</td>
                            <td>{ contact.phone_number }</td>
                            <td>
                                <button onClick={ () => handleUpdate( contact.id ) }  className='btn btn-warning'>Update</button> 
                            </td>
                            <td> 
                                <button 
                                    onClick={ () => handleDelete( contact.id) }  
                                    className='btn btn-danger'>Delete</button> 
                            </td>
                        </tr>
                    )

                })}

               {/*<tr>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>55-5555</td>
                    <td> 
                        <button className='btn btn-warning'>Update</button> 
                    </td>
                    <td> 
                        <button className='btn btn-danger'>Delete</button> 
                    </td>
  </tr>*/} 
            </tbody>
        </table>
    </div>
  )
}
