import React , { useState } from 'react'

export const AddContact = () => {
    const [name, setName] = useState('');
    const [location, setLocation] = useState('');
    const [priceRange, setPriceRange] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
    }
  return (
    <div className='mb-3 container'>
        <form action="" method="post">
            <div className="form-col">
                <div className="row mb-4">
                    <input value={name}  onChange={ e => setName(e.target.value) }  type="text"className='form-control'  placeholder='First name'/>
                </div>
                <div className="row mb-4">
                      <input value={location}  onChange={ e => setLocation(e.target.value) } type="text"className='form-control' placeholder='Last Name' />
                </div>
                <div className="row mb-4">
                      <input value={priceRange}  onChange={ e => setPriceRange(e.target.value) } type="text"className='form-control' placeholder='Phone Number' />
                </div>
            </div> 
            <button  onClick={ handleSubmit }   type='submit' className='btn btn-primary '>Add New Contact</button>      
        </form>
    </div>
  )
}
